import React, { useState } from 'react';
import './App.css';
import image1 from './images/image1.jpg';
import image2 from './images/image2.jpg';
import image3 from './images/image3.jpg';

function App() {
  const [selectedCategory, setSelectedCategory] = useState(null);

  const handleCategoryChange = (category) => {
    setSelectedCategory(category);
  };

  const getCategoryImages = () => {
    switch (selectedCategory) {
      case 'landscape':
        return [image1, image2, image3];
      case 'portraits':
        return [image1, image2, image3];
      case 'nature':
        return [image1, image2, image3];
      default:
        return [];
    }
  };
  return (
    <div className="app">
      <header className="app-header">
        <h1>Photography Portfolio</h1>
        <p>Welcome to my portfolio showcasing my photography work.</p>
      </header>
      <main className="app-main">
        <div className="dropdown">
          <button className="dropbtn">Select Category</button>
          <div className="dropdown-content">
            <button onClick={() => handleCategoryChange('landscape')}>Landscape</button>
            <button onClick={() => handleCategoryChange('portraits')}>Portraits</button>
            <button onClick={() => handleCategoryChange('nature')}>Nature</button>
          </div>
        </div>
        <section className="gallery">
          {getCategoryImages().map((imageUrl, index) => (
            <div className="photo" key={index}>
              <img src={imageUrl} alt={`Photo ${index + 1}`} />
            </div>
          ))}
          </section>
        <section className="about-me">
          <h2>About Me</h2>
          <p>Lorem ipsum dolor sit amet, consectetur adipiscing elit. Nulla vehicula mi eget ligula dictum, sed sodales augue ultrices.</p>
        </section>
        <section className="contact">
          <h2>Contact Me</h2>
          <p>Email: example@example.com</p>
          <p>Phone: 123-456-7890</p>
        </section>
      </main>
      <footer className="app-footer">
        <p>&copy; 2024 Photography Portfolio. All rights reserved.</p>
      </footer>
    </div>
  );
}

export default App;
